package main

import "testing"

func Test_lengthOfLongestSubstring(t *testing.T) {
	testcases := []struct {
		msg       string
		input     string
		expect    int
		expectStr string
	}{
		{
			"space",
			" ",
			1,
			" ",
		},
		{
			"space",
			"ba",
			2,
			"ba",
		},
		{
			"simple case",
			"dvdf",
			3,
			"vdf",
		},
		{
			"simple sample1",
			"abcabcbb",
			3,
			"abc",
		},
		{
			"simple sample2",
			"bbbb",
			1,
			"b",
		},
		{
			"simple sample3",
			"pwwkew",
			3,
			"wke",
		},
		{
			"simple nest",
			"abcdcda",
			4,
			"abc",
		},
		{
			"simple nest2",
			"abcdefghijkcda",
			11,
			"abcdefghijk",
		},
		{
			"innter characters are longest",
			"asjrgapa",
			6,
			"sjrgap",
		},
		{
			"all characters",
			"abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!\"#$%&'()*+,-./:;<=>?@[\\]^_`{|}~ abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!\"#$%&'()*+,-./:;<=>?@[\\]^_`{|}~ ",
			95,
			"abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!\"#$%&'()*+,-./:;<=>?@[\\]^_`{|}~ ",
		},
	}

	for _, tt := range testcases {
		actual := lengthOfLongestSubstring(tt.input)
		if actual != tt.expect {
			t.Errorf("[%s] act: %s(%d), exp: %s(%d)\n", tt.msg, actualStr, actual, tt.expectStr, tt.expect)
		}
	}
}

